mapper.update:
UPDATE BOARD
   SET CATEGORY_NO = #{categoryNo},
		BOARD_TITLE = #{boardTitle},
		BOARD_CONTENT = #{boardContent}
 WHERE BOARD_NO = #{boardNo}
   AND STATUS = 'Y'

UPDATE ATTACHMENT
   SET 	ORIGIN_NAME = #{originName},
		CHANGE_NAME = #{changeName},
		FILE_PATH = #{filePath}
 WHERE FILE_NO = #{fileNo}
